        static void find_element_by_name()
        {
            // -  Load the site;
            // - find the element  by name to enter home vlaue
            // - clean the old value
            // - enter the new value
           //  < input type = "number" step = "any" tabindex = "1" class="right-cell" name="param[homevalue]" value="300000" size="10">
            IWebDriver driver = new FirefoxDriver();
            driver.Url = "http://www.mortgagecalculator.org/";
            try {
                IWebElement elment = driver.FindElement(By.Name("param[homevalue]"));
                if(elment.Displayed)
                {
                    Console.WriteLine("Found the elment param[homevalue]");
                }
                elment.Clear();
                elment.SendKeys("23434");                        
                }
            catch (NoSuchElementException)
            {
                Console.WriteLine(" not found ");
            }
            Console.ReadKey();
            driver.Dispose();
        }

